function FlashMessageManager() {
  this.baseModal = function (title, message, color, icon) {
    $icon_div = $('<i>').addClass('fas').addClass(icon);
    $title_div = $('<div>').html(title);
    $message_div = $('<div>').addClass('gds-modal-body').html(message);

    $elem_close = $('<div>').addClass('gds-modal-close').attr('gds:modal:dismiss', '');
    $elem_title = $('<div>').addClass('gds-modal-title');
    $elem_feedback = $('<div>').addClass('feedback').addClass(color);

    $elem_title.append($elem_feedback);
    $elem_feedback.append($icon_div);
    $elem_title.append($title_div);

    $elem_wrapper = $('<div>').addClass('gds-modal-wrapper gds-modal-feedback');
    $elem_wrapper.append($elem_close);
    $elem_wrapper.append($elem_title);
    if (message) {
      $elem_wrapper.append($message_div);
    }

    $elem = $('<div>').addClass('gds-modal flash');
    $elem.append($elem_wrapper);

    $('body').append($elem);

    return $elem;
  }
}

FlashMessageManager.prototype.success = function (title, message) {
  $('.gds-modal.flash').remove();
  $elem = this.baseModal(title, message, 'check', 'fa-check-circle');
  GDS.modal.open($elem);
}

FlashMessageManager.prototype.error = function (title, message) {
  $('.gds-modal.flash').remove();
  $elem = this.baseModal(title, message, 'error', 'fa-info-circle');
  GDS.modal.open($elem);
}

function FormManager() {
  var FormManager = this;

  this.forms = Array();
  this.defaults = {
    targets: '.form-validate',
    ignore: '.ignore',
    errorClass: 'help-block',
    errorElement : 'div',
    focusInvalid: false,
    errorPlacement: function(error, element) {
      return;
    },
    invalidHandler: function(event, validator) {
      var name = validator.errorList[0].element.name.replace('[', '\\[').replace(']', '\\]');

      var offset = $('[name="' + name + '"]').offset();
      $('html, body').animate({
        scrollTop: (offset.top - 100)
      }, 500, function() {
        $('[name="' + name + '"]').focus();
      });
    },
    highlight: function(element) {
      $(element).addClass('is-invalid');
      
      if( $(element).attr('type') == 'radio' ) {
        var name = $(element).attr('name');
        $(element.form).find('[name="' + name + '"]').addClass('is-invalid');
      }
    },
    unhighlight: function(element) {
      $(element).removeClass('is-invalid');

      if( $(element).attr('type') == 'radio' ) {
        var name = $(element).attr('name');
        $(element.form).find('[name="' + name + '"]').removeClass('is-invalid');
      }
    },
    submitHandler: function (form) {
      $(form).find('[type="submit"]').prop('disabled', 'disabled');
      form.submit();
    }
  };

  this.validate = function (targets){
    $(targets).each(function(){
      FormManager.forms.push($(this).validate(FormManager.defaults));
    });
  }
  $(document).ready(function(){
    FormManager.validate(FormManager.defaults.targets);
  });
}
