function MaskManager() {
    var MaskManager = this;

    this.apply = function (targets) {

        MaskManager.applyMask();

        MaskManager.applyNumeric();

        MaskManager.applyMoney();

    }

    this.applyMask = function () {
        $(".mask-date").mask('00/00/0000');
        $(".mask-daterange").mask('00/00/0000 - 00/00/0000');
        $(".mask-hour").mask('00:00');
        $(".mask-cnpj").mask('00.000.000/0000-00');
        $(".mask-birthday, .birthday").mask('00/00');
        $(".mask-month-year").mask('00/0000');
        $(".mask-cpf").mask('000.000.000-00');
        $(".mask-shoe-size").mask('00');
        $(".mask-person-stature").mask('0,00');
        $(".mask-token-sms").mask('000000');
        $(".mask-cep").mask('00000-000');
        $('.mask-ip').mask('099.099.099.099');
        $(".mask-mannequin-bluse, .mask-mannequin-trousers").mask('ZZZ', {
            translation: {
                'Z': {
                    pattern: /[a-zA-Z0-9]/
                }
            }
        });
        var SPMaskBehavior = function (val) {
            return val.replace(/\D/g, '').length === 11 ? '(00) 00000-0000' : '(00) 0000-00009';
        },
        spOptions = {
            onKeyPress: function (val, e, field, options) {
                field.mask(SPMaskBehavior.apply({}, arguments), options);
            }
        };

        $(".mask-phone, .mask-mobile").mask(SPMaskBehavior, spOptions);
    }

    this.applyNumeric = function () {
        $(".mask-numeric, .numeric").numeric();

        $(".mask-integer, .integer").numeric(false, function () {
            if (this.value) {
                this.value = this.value.toString().replace(/\D+/g, '');
            }
            this.focus();
        });

        $(".mask-positive, .positive").numeric({ negative: false }, function () {
            this.value = "";
            this.focus();
        });

        $(".mask-positive-integer, .positive-integer").numeric({ decimal: false, negative: false }, function () {
            this.value = "";
            this.focus();
        });
    }

    this.applyMoney = function () {
        $(".mask-money, .money").maskMoney({ symbol: '', thousands: '.', decimal: ',', symbolStay: false });
        $(".mask-money-3f").maskMoney({ symbol: '', thousands: '.', decimal: ',', precision: 3, symbolStay: false, allowNegative: true });
        $(".mask-money-4f").maskMoney({ symbol: '', thousands: '.', decimal: ',', precision: 4, symbolStay: false, allowNegative: true });
        $(".mask-money-allow-zero, .money-allow-zero").maskMoney({ symbol: '', thousands: '.', decimal: ',', symbolStay: false, allowZero: true });
        // Depreciado, usar classes acima, validar se está salvando correto no backend ao alterar
        $(".mask-float, .money-float").maskMoney({ symbol: '', thousands: '', decimal: '.', symbolStay: false, allowNegative: true });
        $(".mask-float3, .money-float3").maskMoney({ symbol: '', thousands: '', decimal: '.', precision: 3, symbolStay: false, allowNegative: true });
        $(".mask-float4, .money-float4").maskMoney({ symbol: '', thousands: '', decimal: '.', precision: 4, symbolStay: false, allowNegative: true });

    }


    $(document).ready(function () {
        if ($('body[gds-version="1.0"]').length > 0) {
            MaskManager.apply();
        }
    });
}
function ModalManager() {
  $(document).on('click', '[gds\\:modal\\:toggle]', function (e) {
    e.preventDefault();

    var $that = $(this);
    if (target = $that.attr('gds:modal:target')) {
      var options = {
        url: $that.attr('gds:modal:href'),
        query: $that.attr('gds:modal:query'),
        confirm: $that.attr('gds:modal:confirm-callback'),
        beforeOpen: $that.attr('gds:modal:before-open-callback'),
        $caller: $that
      }

      GDS.modal.open(target, options);
    }
  });

  $(document).on('click', '[gds\\:modal\\:dismiss]', function (e) {
    e.preventDefault();

    var $that = $(this);
    if (modal = $that.parents('.gds-modal.open')) {
      GDS.modal.dismiss(modal);
    }
  });

  $(document).on('click', '[gds\\:modal\\:confirm]', function (e) {
    e.preventDefault();

    var $that = $(this);
    if (modal = $that.parents('.gds-modal.open')) {
      GDS.modal.confirm();
      GDS.modal.dismiss();
    }
  });

  $(document).on('keyup', function (e) {
    if (e.keyCode == 27 && $('.gds-modal.open').length > 0) {
      GDS.modal.dismiss();
    }
  });
}

ModalManager.prototype.open = function (target, options) {
  var that = this;
  this.$modal = $(target);

  if (!options) {
    options = {};
  }

  this.query = options.query;
  this.$caller = options.$caller;

  this.beforeOpen = function() {
    eval(options.beforeOpen);
  }

  this.confirm = function () {
    eval(options.confirm);
  }

  if (this.url = options.url) {
    $.ajax({
      url: options.url,
      data: this.query,
      method: 'GET',
      cache: false,
      beforeSend: function (e) {
        GDS.loading.open();
      },
      complete: function (data) {
        that.$modal.html($(data.responseText));
        that.beforeOpen();
        GDS.loading.close();
        that.$modal.addClass('open');
      }
    });
  } else {
    that.beforeOpen();
    this.$modal.addClass('open');
  }
}

ModalManager.prototype.dismiss = function (target) {
  if (target) {
    var $target = $(target);
    $target.removeClass('open');
  } else {
    this.$modal.removeClass('open');
  }
}
