

function ChartManager() {
  var ChartManager = this;

  Chart.defaults.global.maintainAspectRatio = false;
  Chart.defaults.global.responsive = true;
  Chart.defaults.global.elements.point.radius = 0;
  Chart.defaults.global.elements.line.tension = 0;

  Chart.defaults.global.legend.position = 'bottom';
  Chart.defaults.global.legend.labels.fontSize = 14;
  Chart.defaults.global.legend.labels.usePointStyle = true;
  Chart.defaults.global.legend.labels.boxWidth = 5;
  Chart.defaults.global.legend.labels.padding = 20;

  Chart.defaults.global.tooltips.intersect = false; // Só mostra quando estiver com o mouse em cima do item
  Chart.defaults.global.tooltips.mode = 'index'; // Só mostra quando estiver com o mouse em cima do item
  // Chart.defaults.global.tooltips.enabled = false; // Option para customizar a tooltip

  Chart.defaults.global.tooltips.displayColors = true;
  Chart.defaults.global.tooltips.bodySpacing = 3;
  Chart.defaults.global.tooltips.backgroundColor =  '#FFFFFF';
  Chart.defaults.global.tooltips.titleFontColor =  '#000000';
  Chart.defaults.global.tooltips.titleFontStyle =  'bold';
  Chart.defaults.global.tooltips.titleFontSize =  14;
  Chart.defaults.global.tooltips.titleMarginBottom =  8;
  Chart.defaults.global.tooltips.bodyFontColor =  '#676870';
  Chart.defaults.global.tooltips.bodyFontSize =  14;
  // Chart.defaults.global.tooltips.bodyFontStyle =  'bold';
  Chart.defaults.global.tooltips.borderColor =  '#D6D7DC';
  Chart.defaults.global.tooltips.borderWidth =  1;
  Chart.defaults.global.tooltips.xPadding =  10;
  Chart.defaults.global.tooltips.yPadding =  10;
  Chart.defaults.global.tooltips.bodySpacing =  5;

  // Chart.defaults.global.tooltips.caretSize =  0;
  // Chart.defaults.global.tooltips.yAlign = 'bottom';

  Chart.defaults.scale.ticks.padding = 10;
  // Chart.defaults.scale.ticks.maxRotation = 0;
  Chart.defaults.scale.ticks.maxTicksLimit = 8;
  Chart.defaults.scale.ticks.fontColor = '#838891';
  
  Chart.defaults.scale.gridLines.color = '#E2E2E6';
  Chart.defaults.scale.gridLines.zeroLineColor = '#E2E2E6';
  Chart.defaults.scale.gridLines.drawBorder = false;
  Chart.defaults.scale.gridLines.drawTicks = false;
  Chart.defaults.scale.gridLines.drawOnChartArea = true;

  Chart.defaults.global.plugins.datalabels.display = false;
}