function DaterangepickerManager() {
  var DaterangepickerManager = this;

  this.targets = '.daterange';

  this.apply = function (targets) {
    DaterangepickerManager.datepickers = $( targets ).daterangepicker(DaterangepickerManager.defaults);
  }

  $(document).ready(function(){
    DaterangepickerManager.defaults = {
      "showDropdowns": true,
        "opens": 'left',
        "linkedCalendars": false,
        "alwaysShowCalendars": true,
        "applyButtonClasses": "btn-primary",
        "cancelClass": "btn-outline-secondary",
        "showCustomRangeLabel": false,
        // "maxDate": moment(),
        // "startDate": moment().subtract(29, 'days'),
        // "endDate": moment(),
        "ranges": {
          'Últimos 3 meses': [moment().subtract(3, 'month'), moment()],
          'Últimos 30 dias': [moment().subtract(29, 'days'), moment()],
          'Últimos 7 dias': [moment().subtract(6, 'days'), moment()],
          // 'Mes passado': [moment().subtract(1, 'month').startOf('month'), moment().subtract(1, 'month').endOf('month')],
          'Esse mês': [moment().startOf('month'), moment()],
          'Essa semana': [moment().startOf('week'), moment().endOf('week')],
          'Ontem': [moment().subtract(1, 'days'), moment().subtract(1, 'days')],
          'Hoje': [moment(), moment()]
        },
        "locale": {
          "format": "DD/MM/YYYY",
          "separator": " - ",
          "applyLabel": "Aplicar",
          "cancelLabel": "Cancelar",
          "fromLabel": "De",
          "toLabel": "Até",
          "customRangeLabel": "Personalizado",
          "weekLabel": "S",
          "daysOfWeek": [
            "Dom",
            "Seg",
            "Ter",
            "Qua",
            "Qui",
            "Sex",
            "Sáb"
          ],
          "monthNames": [
            "Janeiro",
            "Fevereiro",
            "Março",
            "Abril",
            "Maio",
            "Junho",
            "Julho",
            "Agosto",
            "Setembro",
            "Outubro",
            "Novembro",
            "Dezembro"
          ],
          "firstDay": 0
        }
      };
      
    DaterangepickerManager.apply(DaterangepickerManager.targets);
  });
}