
function FormManager() {
  var FormManager = this;

  this.forms = Array();
  this.defaults = {
    targets: '.form-validate',
    ignore: '.ignore',
    errorClass: 'help-block',
    errorElement : 'div',
    focusInvalid: false,
    errorPlacement: function(error, element) {
      return;
    },
    invalidHandler: function(event, validator) {
      var name = validator.errorList[0].element.name.replace('[', '\\[').replace(']', '\\]');

      var offset = $('[name="' + name + '"]').offset();
      $('html, body').animate({
        scrollTop: (offset.top - 100)
      }, 500, function() {
        $('[name="' + name + '"]').focus();
      });
    },
    highlight: function(element) {
      $(element).addClass('is-invalid');
      
      if( $(element).attr('type') == 'radio' ) {
        var name = $(element).attr('name');
        $(element.form).find('[name="' + name + '"]').addClass('is-invalid');
      }
    },
    unhighlight: function(element) {
      $(element).removeClass('is-invalid');

      if( $(element).attr('type') == 'radio' ) {
        var name = $(element).attr('name');
        $(element.form).find('[name="' + name + '"]').removeClass('is-invalid');
      }
    },
    submitHandler: function (form) {
      $(form).find('[type="submit"]').prop('disabled', 'disabled');
      form.submit();
    }
  };

  this.validate = function (targets){
    $(targets).each(function(){
      FormManager.forms.push($(this).validate(FormManager.defaults));
    });
  }
  $(document).ready(function(){
    FormManager.validate(FormManager.defaults.targets);
  });
}