function TailManager() {
  var TailManager = this;

  this.targets = '.tail';
  this.selects = Array();

  this.defaults = {
    locale: 'pt_BR',
    height: 350,
    searchConfig: [ "text", "any"]
  };

  this.apply = function (targets) {
    if( typeof tail !== 'undefined' ) {
      $(targets).each(function(){
        var identifier = this.getAttribute('id') ? this.getAttribute('id') : this.getAttribute('name');
        var name = this.getAttribute('name');

        TailManager.selects[identifier] = tail.select('[name="' + name + '"]', TailManager.defaults);
      });
    }
  }

  $(document).ready(function(){
    TailManager.apply(TailManager.targets);
  });
}