function TooltipsterManager() {
  var TooltipsterManager = this;

  this.targets = '.tooltipster:not(.tooltipstered)';
  this.defaults = { interactive: true, animationDuration: 100, delay: 100 };

  this.apply = function (targets = this.targets) {
    TooltipsterManager.tooltips = $( targets ).tooltipster(TooltipsterManager.defaults);
  }

  $(document).ready(function(){
    TooltipsterManager.apply(TooltipsterManager.targets);
  });
}