const buttonSettings = {
  props: {
    variant: {
      type: String,
      default: 'primary'
    },
    loading: {
      type: Boolean,
      default: false
    },
  },
  methods: {
    clicked: function(event) {
     this.$emit('click'); 
   }
  },
  template: `
    <button type="button" class="btn" :class="['btn-' + variant]" @click="clicked" :disabled="loading">
      <div v-if="loading === true" class="spinner-border spinner-border-sm text-white">
        <span class="sr-only">Carregando...</span>
      </div>
      <span v-else><slot></slot></span>
    </button>
  `
};

Vue.component('gds-btn', buttonSettings);
Vue.component('gds-button', buttonSettings);