
window.CardMixin = {
  props: {
    filters: {
      type: Object,
      default: {}
    },
    static: {
      type: Function,
      default: false
    }
  },
  methods: {
    fetchData: function() {},
    fetchStaticData: function() {}
  },
  watch: {
    filters: {
      handler: function() {
        if( !this.static ) {
          this.fetchData();
        }
      },
      deep: true
    },
    static: function(newValue) {
      if( !newValue ) {
        this.fetchData();
      } else {
        this.fetchStaticData();
      }
    }
  },
  mounted: function () {
    if( !this.static ) {
      this.fetchData();
    } else {
      this.fetchStaticData();
    }
  }
};